      return apiRequest("PATCH", `/api/blog/${id}`, { published });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({
        title: "Success",
        description: "Publication status updated",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update publication status",
        variant: "destructive",
      });
    },
  });

