src/components/case-studies.tsx.content:
"use client";

import { motion, useReducedMotion } from "framer-motion";
import { ArrowUpRight } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import Image from "next/image";
import { useState } from "react";

const projects = [
  {
    id: 1,
    title: "E-Commerce Platform",
    client: "RetailMax",
    description:
      "A complete e-commerce solution with real-time inventory, AI-powered recommendations, and seamless checkout experience.",
    image: "/generated_images/e-commerce_project_mockup.png",
    metrics: [
      { label: "Revenue Increase", value: "340%" },
      { label: "User Retention", value: "85%" },
    ],
    tags: ["Next.js", "TypeScript", "Stripe", "PostgreSQL"],
    featured: true,
    gradient: "from-blue-500/10 via-purple-500/5 to-pink-500/10",
  },
  {
    id: 2,
    title: "SaaS Analytics Dashboard",
    client: "DataFlow Inc",
    description:
      "Enterprise-grade analytics platform with real-time data visualization and custom reporting.",
    image: "/generated_images/saas_dashboard_project_mockup.png",
    metrics: [
      { label: "Data Processing", value: "10M+" },
      { label: "Uptime", value: "99.9%" },
    ],
    tags: ["React", "Node.js", "AWS", "D3.js"],
    featured: false,
    gradient: "from-emerald-500/10 via-teal-500/5 to-cyan-500/10",
  },
  {
    id: 3,
    title: "Healthcare Mobile App",
    client: "MedConnect",
    description:
      "HIPAA-compliant mobile app for patient management and telemedicine consultations.",
    image: "/generated_images/healthcare_app_project_mockup.png",
    metrics: [
      { label: "App Downloads", value: "500K+" },
      { label: "User Rating", value: "4.9" },
    ],
    tags: ["React Native", "Firebase", "HealthKit"],
    featured: false,
    gradient: "from-orange-500/10 via-red-500/5 to-rose-500/10",
  },
];

function ProjectCard({
  project,
  isFeatured,
}: {
  project: (typeof projects)[0];
  isFeatured: boolean;
}) {
  const [isHovered, setIsHovered] = useState(false);
  const prefersReducedMotion = useReducedMotion();

  return (
    <motion.div
      initial={{ opacity: 0, y: prefersReducedMotion ? 0 : 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: prefersReducedMotion ? 0 : 0.6, ease: [0.25, 0.46, 0.45, 0.94] }}
      viewport={{ once: true }}
      className={isFeatured ? "lg:col-span-2 lg:row-span-2" : ""}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Card
        className={`group h-full overflow-hidden cursor-pointer border-border/50 transition-all duration-500 ${
          isHovered ? "border-primary/30 glow-md" : ""
        }`}
        data-testid={isFeatured ? "card-project-featured" : `card-project-${project.id}`}
      >
        <div
          className={`relative ${isFeatured ? "h-64 lg:h-80" : "h-48"} overflow-hidden`}
        >
          <div className="absolute inset-0">
            <Image
              src={project.image}
              alt={project.title}
              fill
              className={`object-cover transition-transform duration-600 ${!prefersReducedMotion && isHovered ? 'scale-110' : 'scale-100'}`}
            />
          </div>

          <div
            className={`absolute inset-0 bg-gradient-to-br ${project.gradient} transition-opacity duration-400 ${isHovered ? 'opacity-60' : 'opacity-0'}`}
          />

          <div className="absolute inset-0 bg-gradient-to-t from-card via-card/50 to-transparent" />

          <div
            className={`absolute top-4 right-4 w-10 h-10 rounded-full bg-background/80 backdrop-blur-sm flex items-center justify-center transition-all duration-300 ${isHovered ? 'opacity-100 scale-100' : 'opacity-0 scale-50'}`}
          >
            <ArrowUpRight className="w-5 h-5 text-primary" />
          </div>
        </div>

        <div className={isFeatured ? "p-8" : "p-6"}>
          <div className="flex items-center gap-2 mb-4">
            {isFeatured && (
              <Badge variant="secondary" className="text-xs">
                Featured
              </Badge>
            )}
            <span className="text-sm text-muted-foreground">{project.client}</span>
          </div>

          <motion.h3
            className={`font-display ${
              isFeatured ? "text-2xl" : "text-lg"
            } font-bold text-foreground mb-3 flex items-center gap-2`}
            animate={{ x: isHovered ? 5 : 0 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {project.title}
            <motion.span
              animate={{ opacity: isHovered ? 1 : 0, x: isHovered ? 0 : -10 }}
              transition={{ duration: 0.2 }}
            >
              <ArrowUpRight className="w-5 h-5 text-primary" />
            </motion.span>
          </motion.h3>

          <p
            className={`text-muted-foreground ${
              isFeatured ? "mb-6 leading-relaxed" : "text-sm line-clamp-2 mb-4"
            }`}
          >
            {project.description}
          </p>

          {isFeatured && (
            <motion.div
              className="flex flex-wrap gap-6 mb-6"
              initial={{ opacity: 0 }}
              whileInView={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
              viewport={{ once: true }}
            >
              {project.metrics.map((metric, index) => (
                <motion.div
                  key={metric.label}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.4 + index * 0.1 }}
                  viewport={{ once: true }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="text-2xl font-bold gradient-text">
                    {metric.value}
                  </div>
                  <div className="text-sm text-muted-foreground">
                    {metric.label}
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}

          <div className="flex flex-wrap gap-2">
            {(isFeatured ? project.tags : project.tags.slice(0, 3)).map(
              (tag, index) => (
                <motion.div
                  key={tag}
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  transition={{ delay: 0.1 * index }}
                  viewport={{ once: true }}
                  whileHover={{ scale: 1.05, y: -2 }}
                >
                  <Badge variant="outline" className="text-xs hover:border-primary/50 transition-colors">
                    {tag}
                  </Badge>
                </motion.div>
              )
            )}
          </div>
        </div>
      </Card>
    </motion.div>
  );
}

export function CaseStudies() {
  const featured = projects.find((p) => p.featured);
  const others = projects.filter((p) => !p.featured);

  return (
    <section id="work" className="py-20 md:py-32 relative overflow-hidden">
      <div
        className="absolute top-1/2 right-0 w-[300px] h-[300px] rounded-full opacity-5 blur-3xl pointer-events-none -translate-y-1/2"
        style={{
          background:
            "radial-gradient(circle, hsl(280 65% 65%) 0%, transparent 70%)",
        }}
      />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true }}
          className="text-center max-w-3xl mx-auto mb-16"
        >
          <motion.span
            className="text-primary font-medium text-sm tracking-wider uppercase"
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            Our Work
          </motion.span>
          <motion.h2
            className="font-display text-3xl sm:text-4xl md:text-5xl font-bold text-foreground mt-4 mb-6"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
            viewport={{ once: true }}
          >
            Featured <span className="gradient-text">Case Studies</span>
          </motion.h2>
          <motion.p
            className="text-muted-foreground text-lg leading-relaxed"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Real results for real businesses. Explore how we've helped our
            clients achieve their digital transformation goals.
          </motion.p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {featured && <ProjectCard project={featured} isFeatured={true} />}
          {others.map((project) => (
            <ProjectCard key={project.id} project={project} isFeatured={false} />
          ))}
        </div>
      </div>
    </section>
  );
}

src/components/footer.tsx.content:
"use client";

import { motion } from "framer-motion";
import Link from "next/link";
import { Code2 } from "lucide-react";
import { SiGithub, SiLinkedin, SiX } from "react-icons/si";
import { useState } from "react";

type FooterLink = {
  label: string;
  href: string;
  isPage?: boolean;
};

const footerLinks = {
  services: [
    { label: "Web Development", href: "#services" },
    { label: "Mobile Apps", href: "#services" },
    { label: "Cloud Solutions", href: "#services" },
    { label: "SEO & Marketing", href: "#services" },
  ] as FooterLink[],
  company: [
    { label: "About Us", href: "#about" },
    { label: "Our Work", href: "#work" },
    { label: "Blog", href: "/blog", isPage: true },
    { label: "Contact", href: "#contact" },
  ] as FooterLink[],
  resources: [
    { label: "Blog Admin", href: "/blog/admin", isPage: true },
    { label: "Pricing", href: "#pricing" },
    { label: "Process", href: "#process" },
  ] as FooterLink[],
  social: [
    { icon: SiGithub, label: "GitHub", href: "https://github.com" },
    { icon: SiLinkedin, label: "LinkedIn", href: "https://linkedin.com" },
    { icon: SiX, label: "X", href: "https://x.com" },
  ],
};

function AnimatedLink({
  link,
  onClick,
}: {
  link: FooterLink;
  onClick: () => void;
}) {
  const [isHovered, setIsHovered] = useState(false);

  if (link.isPage) {
    return (
      <Link
        href={link.href}
        className="relative text-muted-foreground hover:text-foreground transition-colors text-sm inline-block"
        data-testid={`link-footer-${link.label.toLowerCase().replace(/\s+/g, "-")}`}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <motion.span
          animate={{ x: isHovered ? 3 : 0 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          {link.label}
        </motion.span>
        <motion.div
          className="absolute bottom-0 left-0 h-px bg-gradient-to-r from-primary to-purple-500"
          initial={{ width: 0 }}
          animate={{ width: isHovered ? "100%" : 0 }}
          transition={{ duration: 0.2 }}
        />
      </Link>
    );
  }

  return (
    <button
      onClick={onClick}
      className="relative text-muted-foreground hover:text-foreground transition-colors text-sm inline-block text-left"
      data-testid={`link-footer-${link.label.toLowerCase().replace(/\s+/g, "-")}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <motion.span
        animate={{ x: isHovered ? 3 : 0 }}
        transition={{ type: "spring", stiffness: 300 }}
      >
        {link.label}
      </motion.span>
      <motion.div
        className="absolute bottom-0 left-0 h-px bg-gradient-to-r from-primary to-purple-500"
        initial={{ width: 0 }}
        animate={{ width: isHovered ? "100%" : 0 }}
        transition={{ duration: 0.2 }}
      />
    </button>
  );
}

function SocialButton({
  social,
}: {
  social: (typeof footerLinks.social)[0];
}) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.a
      href={social.href}
      target="_blank"
      rel="noopener noreferrer"
      className={`w-10 h-10 rounded-lg flex items-center justify-center text-muted-foreground transition-all duration-300 ${
        isHovered ? "text-foreground bg-primary/10 glow-sm" : "bg-muted"
      }`}
      data-testid={`link-social-${social.label.toLowerCase()}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      whileHover={{ scale: 1.1, y: -2 }}
      whileTap={{ scale: 0.95 }}
    >
      <motion.div
        animate={{ rotate: isHovered ? [0, -10, 10, 0] : 0 }}
        transition={{ duration: 0.3 }}
      >
        <social.icon className="w-5 h-5" />
      </motion.div>
      <span className="sr-only">{social.label}</span>
    </motion.a>
