  --shadow-2xl: 0px 2px 0px 0px hsl(220 13% 16% / 0.00);

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --primary-border: hsl(var(--primary));
  --secondary-border: hsl(var(--secondary));
  --muted-border: hsl(var(--muted));
  --accent-border: hsl(var(--accent));
  --destructive-border: hsl(var(--destructive));
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }

  /* Gradient text utility */
  .gradient-text {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(217 91% 75%) 50%, hsl(280 65% 65%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-text-animated {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(217 91% 75%) 25%, hsl(280 65% 65%) 50%, hsl(217 91% 75%) 75%, hsl(var(--primary)) 100%);
    background-size: 200% auto;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: gradient-shift 4s ease-in-out infinite;
  }

  @keyframes gradient-shift {
    0%, 100% { background-position: 0% center; }
    50% { background-position: 200% center; }
  }

  /* Glow effects */
  .glow-sm {
    box-shadow: 0 0 15px -3px hsl(var(--primary) / 0.3);
  }

  .glow-md {
    box-shadow: 0 0 25px -5px hsl(var(--primary) / 0.4);
  }

  .glow-lg {
    box-shadow: 0 0 40px -10px hsl(var(--primary) / 0.5);
  }

  .hover-glow:hover {
    box-shadow: 0 0 30px -5px hsl(var(--primary) / 0.4);
  }

  /* Gradient border */
  .gradient-border {
    position: relative;
    background: hsl(var(--card));
    border-radius: inherit;
  }

  .gradient-border::before {
    content: "";
    position: absolute;
    inset: 0;
    padding: 1px;
    border-radius: inherit;
    background: linear-gradient(135deg, hsl(var(--primary) / 0.5) 0%, hsl(280 65% 65% / 0.5) 100%);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  /* Shimmer effect */
  .shimmer {
    position: relative;
    overflow: hidden;
  }

  .shimmer::after {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(
      90deg,
      transparent 0%,
      hsl(var(--primary) / 0.1) 50%,
      transparent 100%
    );
    transform: translateX(-100%);
    animation: shimmer 3s infinite;
  }

  @keyframes shimmer {
    100% { transform: translateX(100%); }
  }

  /* Floating animation */
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-float-delayed {
    animation: float 3s ease-in-out infinite;
    animation-delay: 1.5s;
  }

  /* Pulse glow */
  @keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 20px -5px hsl(var(--primary) / 0.3); }
    50% { box-shadow: 0 0 40px -5px hsl(var(--primary) / 0.6); }
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  /* Subtle gradient backgrounds */
  .bg-gradient-subtle {
    background: linear-gradient(135deg, hsl(var(--background)) 0%, hsl(var(--primary) / 0.03) 100%);
  }

  .bg-gradient-radial {
    background: radial-gradient(ellipse at center, hsl(var(--primary) / 0.05) 0%, transparent 70%);
  }

  /* Card hover lift effect */
  .card-hover-lift {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .card-hover-lift:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px -15px hsl(var(--primary) / 0.2);
  }

  /* Smooth underline animation */
  .link-underline {
    position: relative;
  }

  .link-underline::after {
    content: "";
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, hsl(var(--primary)) 0%, hsl(280 65% 65%) 100%);
    transition: width 0.3s ease;
  }

  .link-underline:hover::after {
    width: 100%;
  }

  /* Icon bounce on hover */
  .icon-bounce:hover {
    animation: icon-bounce 0.4s ease;
  }

  @keyframes icon-bounce {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.2); }
  }

  /* Stagger fade in */
  @keyframes stagger-fade-in {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

/* Reduced motion accessibility */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  .animate-float,
  .animate-float-delayed,
  .animate-pulse-glow,
  .shimmer,
  .gradient-text-animated {
    animation: none !important;
  }

  .gradient-text-animated {
    background-position: 0% center !important;
    background-size: 100% auto !important;
  }

  .shimmer::after {
    animation: none !important;
    display: none !important;
    content: none !important;
  }

  .icon-bounce:hover {
    animation: none !important;
  }
}

