src/app/blog/admin/page.tsx.content:
"use client";

import { useState } from "react";
import { motion } from "framer-motion";
import { useQuery, useMutation } from "@tanstack/react-query";
import Link from "next/link";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Plus,
  Pencil,
  Trash2,
  Eye,
  EyeOff,
  ArrowLeft,
  Save,
  X,
} from "lucide-react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Navigation } from "@/components/navigation";
import { Footer } from "@/components/footer";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { insertBlogPostSchema, type BlogPost, type InsertBlogPost } from "@shared/schema";
import { format } from "date-fns";
import { z } from "zod";

const categories = [
  "Technology",
  "Development",
  "Design",
  "Marketing",
  "Case Study",
  "News",
];

const formSchema = insertBlogPostSchema.extend({
  title: z.string().min(5, "Title must be at least 5 characters"),
  excerpt: z.string().min(20, "Excerpt must be at least 20 characters"),
  content: z.string().min(100, "Content must be at least 100 characters"),
});

type FormValues = z.infer<typeof formSchema>;

export default function BlogAdmin() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingPost, setEditingPost] = useState<BlogPost | null>(null);

  const { data: posts, isLoading } = useQuery<BlogPost[]>({
    queryKey: ["/api/blog"],
    queryFn: async () => {
      const response = await fetch("/api/blog?all=true");
      if (!response.ok) throw new Error("Failed to fetch posts");
      return response.json();
    },
  });

  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      coverImage: "",
      category: "Technology",
      author: "NexaTech Team",
      published: false,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertBlogPost) => {
      return apiRequest("POST", "/api/blog", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Blog post created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create blog post",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertBlogPost> }) => {
      return apiRequest("PATCH", `/api/blog/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      setIsDialogOpen(false);
      setEditingPost(null);
      form.reset();
      toast({
        title: "Success",
        description: "Blog post updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update blog post",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/blog/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({
        title: "Success",
        description: "Blog post deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete blog post",
        variant: "destructive",
      });
    },
  });

  const togglePublishMutation = useMutation({
    mutationFn: async ({ id, published }: { id: string; published: boolean }) => {
      return apiRequest("PATCH", `/api/blog/${id}`, { published });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({
        title: "Success",
        description: "Publication status updated",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update publication status",
        variant: "destructive",
      });
    },
  });

  const openCreateDialog = () => {
    setEditingPost(null);
    form.reset({
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      coverImage: "",
      category: "Technology",
      author: "NexaTech Team",
      published: false,
    });
    setIsDialogOpen(true);
  };

  const openEditDialog = (post: BlogPost) => {
    setEditingPost(post);
    form.reset({
      title: post.title,
      slug: post.slug,
      excerpt: post.excerpt,
      content: post.content,
      coverImage: post.coverImage || "",
      category: post.category,
      author: post.author,
      published: post.published,
    });
    setIsDialogOpen(true);
  };

  const generateSlug = (title: string) => {
    return title
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, "-")
      .replace(/(^-|-$)/g, "");
  };

  const onSubmit = (data: FormValues) => {
    if (editingPost) {
      updateMutation.mutate({ id: editingPost.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <Navigation />
      <main className="pt-32 pb-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <div className="flex flex-wrap items-center justify-between gap-4 mb-8">
              <div>
                <Link href="/">
                  <Button variant="ghost" className="-ml-4 mb-4">
                    <ArrowLeft className="w-4 h-4 mr-2" />
                    Back to Home
                  </Button>
                </Link>
                <h1 className="font-display text-3xl font-bold text-foreground">
                  Blog Management
                </h1>
                <p className="text-muted-foreground mt-2">
                  Create and manage your blog posts
                </p>
              </div>
              <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogTrigger asChild>
                  <Button onClick={openCreateDialog} data-testid="button-new-post">
                    <Plus className="w-4 h-4 mr-2" />
                    New Post
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>
                      {editingPost ? "Edit Post" : "Create New Post"}
                    </DialogTitle>
                  </DialogHeader>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                      <FormField
                        control={form.control}
                        name="title"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Title</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                onChange={(e) => {
                                  field.onChange(e);
                                  if (!editingPost) {
                                    form.setValue("slug", generateSlug(e.target.value));
                                  }
                                }}
                                data-testid="input-post-title"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="slug"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Slug</FormLabel>
                            <FormControl>
                              <Input {...field} data-testid="input-post-slug" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="category"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Category</FormLabel>
                              <Select
                                onValueChange={field.onChange}
                                defaultValue={field.value}
                              >
                                <FormControl>
                                  <SelectTrigger data-testid="select-category">
                                    <SelectValue placeholder="Select category" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  {categories.map((cat) => (
                                    <SelectItem key={cat} value={cat}>
                                      {cat}
                                    </SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="author"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Author</FormLabel>
                              <FormControl>
                                <Input {...field} data-testid="input-post-author" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                      <FormField
                        control={form.control}
                        name="coverImage"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Cover Image URL (optional)</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                value={field.value ?? ""}
                                placeholder="https://example.com/image.jpg"
                                data-testid="input-post-cover"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="excerpt"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Excerpt</FormLabel>
                            <FormControl>
                              <Textarea
                                {...field}
                                rows={3}
                                data-testid="input-post-excerpt"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="content"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Content</FormLabel>
                            <FormControl>
                              <Textarea
                                {...field}
                                rows={12}
                                placeholder="Use ## for headings and ### for subheadings"
                                data-testid="input-post-content"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="published"
                        render={({ field }) => (
                          <FormItem className="flex items-center justify-between">
                            <div>
                              <FormLabel>Published</FormLabel>
                              <p className="text-sm text-muted-foreground">
                                Make this post visible to the public
                              </p>
                            </div>
                            <FormControl>
                              <Switch
                                checked={field.value}
                                onCheckedChange={field.onChange}
                                data-testid="switch-published"
                              />
                            </FormControl>
                          </FormItem>
                        )}
                      />
                      <div className="flex justify-end gap-3 pt-4">
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setIsDialogOpen(false)}
                        >
                          <X className="w-4 h-4 mr-2" />
                          Cancel
                        </Button>
                        <Button
                          type="submit"
                          disabled={createMutation.isPending || updateMutation.isPending}
                          data-testid="button-save-post"
                        >
                          <Save className="w-4 h-4 mr-2" />
                          {editingPost ? "Update Post" : "Create Post"}
                        </Button>
                      </div>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </div>

            {isLoading ? (
              <div className="space-y-4">
                {[1, 2, 3].map((i) => (
                  <Card key={i} className="p-6 animate-pulse">
                    <div className="h-6 bg-muted rounded w-1/3 mb-4" />
                    <div className="h-4 bg-muted rounded w-2/3" />
                  </Card>
                ))}
              </div>
            ) : posts && posts.length > 0 ? (
              <div className="space-y-4">
                {posts.map((post) => (
                  <motion.div
                    key={post.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                  >
                    <Card className="p-6">
                      <div className="flex flex-wrap items-start justify-between gap-4">
                        <div className="flex-1 min-w-0">
                          <div className="flex flex-wrap items-center gap-3 mb-2">
                            <h3 className="font-display text-lg font-semibold text-foreground truncate">
                              {post.title}
                            </h3>
                            <Badge
                              variant={post.published ? "default" : "secondary"}
                            >
                              {post.published ? (
                                <>
                                  <Eye className="w-3 h-3 mr-1" />
                                  Published
                                </>
                              ) : (
                                <>
                                  <EyeOff className="w-3 h-3 mr-1" />
                                  Draft
                                </>
                              )}
                            </Badge>
                          </div>
                          <p className="text-sm text-muted-foreground line-clamp-1 mb-2">
                            {post.excerpt}
                          </p>
                          <div className="flex flex-wrap items-center gap-4 text-xs text-muted-foreground">
                            <span>{post.category}</span>
                            <span>{post.author}</span>
                            <span>
                              {format(new Date(post.createdAt), "MMM d, yyyy")}
                            </span>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() =>
                              togglePublishMutation.mutate({
                                id: post.id,
                                published: !post.published,
                              })
                            }
                            data-testid={`button-toggle-publish-${post.id}`}
                          >
                            {post.published ? (
                              <EyeOff className="w-4 h-4" />
                            ) : (
                              <Eye className="w-4 h-4" />
                            )}
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() => openEditDialog(post)}
                            data-testid={`button-edit-${post.id}`}
                          >
                            <Pencil className="w-4 h-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() => deleteMutation.mutate(post.id)}
                            data-testid={`button-delete-${post.id}`}
                          >
                            <Trash2 className="w-4 h-4 text-destructive" />
                          </Button>
                        </div>
                      </div>
                    </Card>
                  </motion.div>
                ))}
              </div>
            ) : (
              <Card className="p-12 text-center">
                <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                  <Plus className="w-8 h-8 text-muted-foreground" />
                </div>
                <h3 className="font-display text-lg font-semibold text-foreground mb-2">
                  No blog posts yet
                </h3>
                <p className="text-muted-foreground mb-4">
                  Create your first post to get started
                </p>
                <Button onClick={openCreateDialog}>
                  <Plus className="w-4 h-4 mr-2" />
                  Create First Post
                </Button>
              </Card>
            )}
          </motion.div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

src/components/navigation.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Link from "next/link";
import { usePathname, useRouter } from "next/navigation";
import { Menu, X, Sun, Moon } from "lucide-react";
import Image from "next/image";
import { Button } from "@/components/ui/button";
import { useTheme } from "@/components/theme-provider";

type NavItem = {
        label: string;
        href: string;
        isPage?: boolean;
};

const navItems: NavItem[] = [
        { label: "Services", href: "#services" },
        { label: "Work", href: "#work" },
        { label: "Pricing", href: "#pricing" },
        { label: "Blog", href: "/blog", isPage: true },
        { label: "About", href: "/about", isPage: true },
        { label: "Contact", href: "#contact" },
];

export function Navigation() {
        const [isScrolled, setIsScrolled] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
        const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);
        const { theme, setTheme } = useTheme();
        const pathname = usePathname();
        const router = useRouter();

        useEffect(() => {
                const handleScroll = () => {
                        setIsScrolled(window.scrollY > 20);
                };
                window.addEventListener("scroll", handleScroll);
                return () => window.removeEventListener("scroll", handleScroll);
        }, []);

        const handleNavClick = (item: NavItem) => {
                if (item.isPage) {
                        router.push(item.href);
                } else {
                        if (pathname !== "/") {
                                router.push("/");
                                setTimeout(() => {
                                        const element = document.querySelector(item.href);
                                        if (element) {
                                                element.scrollIntoView({ behavior: "smooth" });
                                        }
                                }, 100);
                        } else {
                                const element = document.querySelector(item.href);
                                if (element) {
                                        element.scrollIntoView({ behavior: "smooth" });
                                }
                        }
                }
                setIsMobileMenuOpen(false);
        };

        return (
                <>
                        <motion.header
                                initial={{ y: -100 }}
                                animate={{ y: 0 }}
                                transition={{ duration: 0.5, ease: "easeOut" }}
                                className={`fixed top-0 left-0 right-0 z-50 transition-all duration-500 ${
                                        isScrolled
                                                ? "bg-background/70 backdrop-blur-xl border-b border-border/50 shadow-lg shadow-background/5"
                                                : "bg-transparent"
                                }`}
                        >
                                <nav className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                                        <div className="flex items-center justify-between h-16 md:h-20">
                                                <motion.div
                                                        whileHover={{ scale: 1.02 }}
                                                        whileTap={{ scale: 0.98 }}
                                                >
                                                        <Link
                                                                href="/"
                                                                className="flex items-center gap-2 group"
                                                                data-testid="link-logo"
                                                        >
                                                                <motion.div
                                                                        className="w-10 h-10 rounded-lg overflow-hidden relative"
                                                                        whileHover={{ rotate: 5 }}
                                                                        transition={{
                                                                                type: "spring",
                                                                                stiffness: 300,
                                                                        }}
                                                                >
                                                                        <Image
                                                                                src="/logo.png"
                                                                                alt="OneStop Logo"
                                                                                width={40}
                                                                                height={40}
                                                                                className="w-full h-full object-cover"
                                                                        />
                                                                </motion.div>
                                                                <span className="font-display font-bold text-xl text-foreground group-hover:text-primary transition-colors duration-300">
                                                                        OneStop
                                                                </span>
                                                        </Link>
                                                </motion.div>

                                                <div className="hidden md:flex items-center gap-1 relative">
                                                        {navItems.map((item, index) => (
                                                                <motion.button
                                                                        key={item.label}
                                                                        onClick={() => handleNavClick(item)}
                                                                        onMouseEnter={() => setHoveredIndex(index)}
                                                                        onMouseLeave={() => setHoveredIndex(null)}
                                                                        className="relative px-4 py-2 text-sm font-medium text-muted-foreground hover:text-foreground transition-colors rounded-lg"
                                                                        whileTap={{ scale: 0.95 }}
                                                                        data-testid={`link-nav-${item.label.toLowerCase()}`}
                                                                >
                                                                        {hoveredIndex === index && (
                                                                                <motion.div
                                                                                        className="absolute inset-0 bg-primary/10 rounded-lg"
                                                                                        layoutId="navbar-hover"
                                                                                        initial={{ opacity: 0 }}
                                                                                        animate={{ opacity: 1 }}
                                                                                        exit={{ opacity: 0 }}
                                                                                        transition={{
                                                                                                type: "spring",
                                                                                                stiffness: 350,
                                                                                                damping: 30,
                                                                                        }}
                                                                                />
                                                                        )}
                                                                        <span className="relative z-10">
                                                                                {item.label}
                                                                        </span>
                                                                </motion.button>
                                                        ))}
                                                </div>

                                                <div className="hidden md:flex items-center gap-3">
                                                        <motion.div
                                                                whileHover={{ scale: 1.05 }}
                                                                whileTap={{ scale: 0.95 }}
                                                        >
                                                                <Button
                                                                        variant="ghost"
                                                                        size="icon"
                                                                        onClick={() =>
                                                                                setTheme(
                                                                                        theme === "dark" ? "light" : "dark"
                                                                                )
                                                                        }
                                                                        className="relative overflow-hidden"
                                                                        data-testid="button-theme-toggle"
                                                                >
                                                                        <motion.div
                                                                                initial={false}
                                                                                animate={{
                                                                                        rotate: theme === "dark" ? 180 : 0,
                                                                                }}
                                                                                transition={{
                                                                                        duration: 0.5,
                                                                                        type: "spring",
                                                                                }}
                                                                        >
                                                                                <Sun className="h-5 w-5 rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
                                                                                <Moon className="absolute h-5 w-5 rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100 top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2" />
                                                                        </motion.div>
                                                                        <span className="sr-only">
                                                                                Toggle theme
                                                                        </span>
                                                                </Button>
                                                        </motion.div>
                                                        <motion.div
